import React, { useState } from 'react'
import {Button, Form} from "react-bootstrap"
import ApiCliente from "./ApiCliente";
import Menu from '../Menu/Menu';
import TituloClientes from './TituloClientes';
import axio from 'axios';
import { useNavigate } from 'react-router-dom';
import { useEffect } from "react";

export default function EditarCliente() {
    let history = useNavigate();
    const[clientes, setClientes] = useState([]);
    const[id, setId] = useState(null);
    const [companyId, setCompanyId] = useState(null);
    const [company, setCompany] = useState(null);
    const [name, setName] = useState('');
      const [createdBy, setCreatedBy] = useState('');
      const [updatedBy, setUpdatedBy] = useState('');

    useEffect(() => {
        setId(localStorage.getItem('id'))
        setCompanyId(localStorage.getItem('companyId'));
        setCompany(localStorage.getItem('company'));
        setName(localStorage.getItem('name'));
        setCreatedBy(localStorage.getItem('createdBy'));
        setUpdatedBy(localStorage.getItem('updatedBy'));
    }, []);

    const updateAPIData = () => {
    axios.post(`http://simeiapi.doistech.com.br/api/cliente/${companyId}${id}`, {
        company,
        name,
        createdBy,
        updatedBy
      }).then(() => {
          history.push('/Tabela2')
      })
  }
    return (
        <div>
           <Menu/>
    <TituloClientes/>
    <br></br>
                  <Form>
                  <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
        <Form.Label>Nome</Form.Label>
        <Form.Control type="text" placeholder="name" value={company} onChange={e=> setCompany(e.target.value)}/>
      </Form.Group>                 
      <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
        <Form.Label>Nome</Form.Label>
        <Form.Control type="text" placeholder="name" value={name} onChange={e=> setName(e.target.value)}/>
      </Form.Group>
      <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
        <Form.Label>Example textarea</Form.Label>
        <Form.Control type="text" placeholder="name" value={createdBy} onChange={e=> setCreatedBy(e.target.value)} />
      </Form.Group>
      <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea2">
        <Form.Label>Example textarea</Form.Label>
        <Form.Control type="text" placeholder="name" value={updatedBy} onChange={e=> setUpdatedBy(e.target.value)} />
      </Form.Group>
      <Button type='submit' onClick={updateAPIData}>Submit</Button>
    </Form>
        </div>
    )
}
