import Menu from "../Menu/Menu"
import React from "react";
import {Button, Table, Form} from "react-bootstrap"
import { useState, useEffect } from "react";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
/*import cellEditFactory, { Type } from "react-bootstrap-table2-editor";
import filterFactory, { textFilter } from "react-bootstrap-table2-filter";*/
import './TabelaCliente.css'
import ApiCliente from "./ApiCliente";
import Footer from "../Menu/Footer";
import TituloClientes from "./TituloClientes";
import FormCliente from "./FormCliente";
import Modal from "react-modal";


function Clientes() {
  const[clientes, setClientes] = useState([]);

  //Método Get Clientes.
  useEffect(() => {
    ApiCliente.get("/api/cliente/show?companyId=2").then(({data})=> {
    setClientes(data.clientes);
  })
  console.log(clientes)
  //eslin-disable-next-line- react-hooks/exhaustive-deps
}, []);

const[id, setId] = useState("");
const[name, setName] = useState("");
const[createdBy, setCreatedBy] = useState("");
const[updatedBy, setUpdatedBy] = useState(""); 

async function handleSubmit(){
    const data = {id: id,
        name: name, 
        createdBy: createdBy, 
        updatedBy: updatedBy
    }
    const response = await ApiCliente.post('http://simeiapi.doistech.com.br/api/cliente', data)
 if (response.status==200){
    window.location.href='/cliente'
 }else{alert('erro');}
    
    toggleModal();
}



Modal.setAppElement("#root");
const [isOpen, setIsOpen] = useState(false);

  function toggleModal() {
    setIsOpen(!isOpen);
  }
 
return (

        <div>
          <button onClick={toggleModal}>Open modal</button>
          
          <Modal
            isOpen={isOpen}
            onRequestClose={toggleModal}
            contentLabel="My dialog"
          >
            <div>
            <Form>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
        <Form.Label>Email address</Form.Label>
        <Form.Control type="email" placeholder="Id" value={id} onChange={e=> setId(e.target.value)}/>
      </Form.Group>
      <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
        <Form.Label>Email address</Form.Label>
        <Form.Control type="email" placeholder="name" value={name} onChange={e=> setName(e.target.value)}/>
      </Form.Group>
      <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
        <Form.Label>Example textarea</Form.Label>
        <Form.Control type="email" placeholder="name" value={createdBy} onChange={e=> setCreatedBy(e.target.value)} />
      </Form.Group>
      <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
        <Form.Label>Example textarea</Form.Label>
        <Form.Control type="email" placeholder="name" value={updatedBy} onChange={e=> setUpdatedBy(e.target.value)} />
      </Form.Group>
    </Form></div>
            <button onClick={handleSubmit}>Close modal</button>
          </Modal>
          
    

    <Table striped bordered hover>
      <thead>
        <tr>
          <th>#</th>
          <th>First Name</th>
          <th>Last Name</th>
          <th>Username</th>
          <th>Username</th>
        </tr>
      </thead>
      <tbody>
      {
                clientes.map((cliente) =>
                    <tr>
                      <td>{cliente.id}</td>
                      <td>{cliente.name}</td>
                      <td>{cliente.createdBy}</td>
                      <td>{cliente.updatedBy}</td>
                      <td><Button variant="primary" size="sm" onClick={() => this.carregarDados(cliente.id)}>Editar</Button>{' '}
                          <Button variant="danger" size="sm" onClick={() => this.excluirMusica(cliente.id)}>Excluir</Button></td>
                    </tr>
                )
               }
      </tbody>
    </Table>
    </div>
  )


   
   
}
export default Clientes;
