import React from "react";
import { ReactMUIDatatable, metaSymbol } from "react-material-ui-datatable";
import IconButton from "@material-ui/core/IconButton";
import GenderFemaleIcon from "mdi-material-ui/GenderFemale";
import GenderMaleIcon from "mdi-material-ui/GenderMale";
import SwapHorizIcon from "@material-ui/icons/SwapHoriz";
import DeleteIcon from "@material-ui/icons/Delete";
import users from "./stub.json";

const columns = [
  {
    name: "img",
    label: "Image",
    filter: false,
    customCell: ({ value, row }) => <img src={value} height="20" />
  },
  { name: "firstName", label: "First name" },
  { name: "lastName", label: "Last name" },
  { name: "age", label: "Age" },
  {
    name: "gender",
    label: "Gender",
    customCell: ({ value, row }) =>
      value === "Male" ? <GenderMaleIcon /> : <GenderFemaleIcon />
  },
  {
    name: "credits.email",
    label: "Email"
  }
];

const customToolbarSelectActions = ({
  data,
  selectedRows,
  updateSelectedRows,
  handleDelete
}) => (
  <React.Fragment>
    <IconButton
      onClick={() => {
        const nextSelectedRows = data.reduce((nextSelectedRows, row) => {
          if (!selectedRows.includes(row[metaSymbol].rawIndex)) {
            nextSelectedRows.push(row[metaSymbol].rawIndex);
          }

          return nextSelectedRows;
        }, []);

        updateSelectedRows(nextSelectedRows);
      }}
    >
      <SwapHorizIcon />
    </IconButton>
    <IconButton
      onClick={() => {
        handleDelete(selectedRows);
      }}
    >
      <DeleteIcon />
    </IconButton>
  </React.Fragment>
);

const TabelaClientes = () => (
  <div className={"App"}>
    <ReactMUIDatatable
      title={"List"}
      data={users}
      columns={columns}
      toolbarSelectActions={customToolbarSelectActions}
    />
  </div>
);

export default TabelaClientes;
