import Menu from "../Menu/Menu"
import React from "react";
import { Button, Table } from "react-bootstrap"
import { useState, useEffect, useMemo } from "react";
import './TabelaCliente.css'
import Api from "../../services/Api";
import Footer from "../Menu/Footer";
import TituloClientes from "./TituloClientes";
import { Link } from 'react-router-dom';
import axios from "axios";
import Pagination from '../Pagination';


function Clientes() {
  const [clientes, setClientes] = useState([]);

  //Método Get Clientes.
  useEffect(() => {
    Api.get("/api/cliente/show?companyId=2").then(({ data }) => {
      setClientes(data.clientes);
    })
    console.log(clientes)
    //eslin-disable-next-line- react-hooks/exhaustive-deps
  }, []);

  const setData = (data) => {
    let { id, name, createdBy, updatedBy, companyId, address } = data;
    localStorage.setItem('id', id);
    localStorage.setItem('company', companyId);
    localStorage.setItem('name', name);
    localStorage.setItem('createdBy', createdBy);
    localStorage.setItem('updatedBy', updatedBy);
    localStorage.setItem('address', address)
  }

  const getData = () => {
    axios.get(`http://simeiapi.doistech.com.br/api/cliente/show?companyId=2`)
      .then((getData) => {
        setClientes(getData.data);
      })
  }

  const onDelete = (id) => {
    axios.delete(`https://60fbca4591156a0017b4c8a7.mockapi.io/fakeData/${id}`)
      .then(() => {
        getData();
      })
  }

  let PageSize = 10;

  const [currentPage, setCurrentPage] = useState(1);

  const currentTableData = useMemo(() => {
    const firstPageIndex = (currentPage - 1) * PageSize;
    const lastPageIndex = firstPageIndex + PageSize;
    return clientes.slice(firstPageIndex, lastPageIndex);
  }, [currentPage]);







  return (
    <div>
      <Menu />
      <TituloClientes />
      <br></br>
      <Link to='/CadastroClientes'><Button>aDICIONAR NOVO CLIENTE</Button></Link>
      <>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Id</th>
            <th>CompanyId</th>
            <th>Name</th>
            <th>createdBy</th>
            <th>updatedBy</th>
            <th>opções</th>
          </tr>
        </thead>
        <tbody>
          {

             clientes(currentTableData).map(cliente =>{
              return(
              <tr>
                <td>{cliente.id}</td>
                <td>{cliente.companyId}</td>
                <td>{cliente.name}</td>
                <td>{cliente.createdBy}</td>
                <td>{cliente.updatedBy}</td>
                <td><Link to='/EditarCliente'><Button onClick={() => setData(cliente)}>Editar</Button></Link>{' '}
                  <Button variant="danger" size="sm" onClick={() => onDelete(cliente.id)}>Excluir</Button></td>
              </tr>);
})
          }
        </tbody>
      </Table>
      <Pagination
        className="pagination-bar"
        currentPage={currentPage}
        totalCount={clientes.length}
        pageSize={PageSize}
        onPageChange={page => setCurrentPage(page)}
      />
      </>
      <Footer/>
    </div>
  )




}
export default Clientes;
