import Menu from "../Menu/Menu"
import React from "react";
import {Button, Table, Form} from "react-bootstrap"
import { useState, useEffect } from "react";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
/*import cellEditFactory, { Type } from "react-bootstrap-table2-editor";
import filterFactory, { textFilter } from "react-bootstrap-table2-filter";*/
import './TabelaCliente.css'
import ApiCliente from "./ApiCliente";
import Footer from "../Menu/Footer";
import TituloClientes from "./TituloClientes";
import FormCliente from "./FormCliente";
import Modal from "react-modal";


function Clientes() {
  const[clientes, setClientes] = useState([]);

  //Método Get Clientes.
  useEffect(() => {
    ApiCliente.get("/api/cliente/show?companyId=2").then(({data})=> {
    setClientes(data.clientes);
  })
  console.log(clientes)
  //eslin-disable-next-line- react-hooks/exhaustive-deps
}, []);

excluirMusica = (id) => {
    fetch("http://simeiapi.doistech.com.br/api/cliente/show?companyId=2/" + id, {method: 'DELETE'})
    .then(resposta => {
        if(resposta.ok){
        this.buscarMusica();
        }
    })
 }

 carregarDados = (id) =>{
    fetch("http://simeiapi.doistech.com.br/api/cliente/show?companyId=2/" + id, {method: 'GET'})
    .then(resposta => resposta.json())
    .then(cliente => {
        this.setState({
            id: cliente.id,
            updatedBy: cliente.updatedBy,
            createdBy: cliente.createdBy,
            name: cliente.name

        })
        this.abrirModal();
    })
 }

 adicionaMusica = (clientes) => {
    fetch("http://simeiapi.doistech.com.br/api/cliente",      
    {method: 'POST',
    headers: {'content-type' : 'application/json'},
    body: JSON.stringify(clientes)
      })
 .then(resposta => {
    if(resposta.ok){
        this.buscarMusica();
    }else{
        alert('Não foi possivel adicionar a musica');
    }
 })
} 

 editarMusica = (clientes) => {
    fetch("http://simeiapi.doistech.com.br/api/cliente/" + clientes.id,  
    {method: 'POST',
    headers: {'Content-Type' : 'application/json'},
    body: JSON.stringify(clientes)
      })
 .then(resposta => {
    if(resposta.ok){
        this.buscarMusica();
    }else{
        alert('Não foi possivel editar os dados da musica');
    }
 })
 }




Modal.setAppElement("#root");
const [isOpen, setIsOpen] = useState(false);

  function toggleModal() {
    setIsOpen(!isOpen);
  }


/* Metodos form - Inicio */
        atualizaFaixa = (e) => {
            this.setState(
                {name: e.target.value}
            )
           }

           atualizaAutor = (e) => {
            this.setState(
                {createdBy: e.target.value}
            )
           }
           atualizaAlbum = (e) => {
            this.setState(
                {updatedBy: e.target.value}
            )
           }   

           submit = () =>{

        if(this.state.id === 0){
            const cliente = {
                id: 0,
                name: this.state.name,
                createdBy: this.state.createdBy,
               updatedBy: this.state.updatedBy,
            }
            this.adicionaMusica(cliente);
        }else{
            const cliente = {
                id: this.state.id,
                name: this.state.name,
                createdBy: this.state.createdBy,
                updatedBy: this.state.updatedBy,
            }
            this.editarMusica(cliente);
        }

            this.fecharModal();
    }

        novo = () =>{
            this.setState(
                {
                    id: 0,
                    name: "",
                    createdBy: "",
                    updatedBy: ""            
                }
            )
            this.abrirModal();
        }

        fecharModal= () =>{
            this.setState(
                {
                    modalAberto : false
                }
            )
        }

        abrirModal= () =>{
            this.setState(
                {
                    modalAberto : true
                }
            )
        }
            
  /* Metodos form - Fim */



 
return (

        <div>
          <button onClick={toggleModal}>Open modal</button>
          
          <Modal
            isOpen={isOpen}
            onRequestClose={toggleModal}
            contentLabel="My dialog"
          >
            <div>
            <Form>
      <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
        <Form.Label>Email address</Form.Label>
        <Form.Control type="email" placeholder="name@example.com" />
      </Form.Group>
      <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
        <Form.Label>Example textarea</Form.Label>
        <Form.Control as="textarea" rows={3} />
      </Form.Group>
    </Form></div>
            <button onClick={toggleModal}>Close modal</button>
          </Modal>
          
    

    <Table striped bordered hover>
      <thead>
        <tr>
          <th>#</th>
          <th>First Name</th>
          <th>Last Name</th>
          <th>Username</th>
          <th>Username</th>
        </tr>
      </thead>
      <tbody>
      {
                clientes.map((cliente) =>
                    <tr>
                      <td>{cliente.id}</td>
                      <td>{cliente.name}</td>
                      <td>{cliente.createdBy}</td>
                      <td>{cliente.updatedBy}</td>
                      <td><Button variant="primary" size="sm" onClick={() => this.carregarDados(cliente.id)}>Editar</Button>{' '}
                          <Button variant="danger" size="sm" onClick={() => this.excluirMusica(cliente.id)}>Excluir</Button></td>
                    </tr>
                )
               }
      </tbody>
    </Table>
    </div>
  )


   
   
}
export default Clientes;
