import Menu from "../Menu/Menu"
import React from "react";
import {Button} from "react-bootstrap"
import { useState, useEffect } from "react";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
/*import cellEditFactory, { Type } from "react-bootstrap-table2-editor";
import filterFactory, { textFilter } from "react-bootstrap-table2-filter";*/
import './TabelaCliente.css'
import ApiCliente from "./ApiCliente";
import Footer from "../Menu/Footer";
import TituloClientes from "./TituloClientes";


function Clientes() {
  const[clientes, setClientes] = useState([]);

  //Método Get Clientes.
  useEffect(() => {
    ApiCliente.get("/api/cliente/show?companyId=2").then(({data})=> {
    setClientes(data.clientes);
  })
  console.log(clientes)
  //eslin-disable-next-line- react-hooks/exhaustive-deps
}, []);

//Metodo Edita Cliente

 excluirMusica = (id) => {
    fetch("http://simeiapi.doistech.com.br/api/cliente/show?companyId=2" + id, {method: 'DELETE'})
    .then(resposta => {
        if(resposta.ok){
        this.buscarMusica();
        }
    })
 }

 carregarDados = (id) =>{
    fetch("http://simeiapi.doistech.com.br/api/cliente/show?companyId=2" + id, {method: 'GET'})
    .then(resposta => resposta.json())
    .then(musica => {
        this.setState({
            id: clientes.id,
            faixa: clientes.name,
            autor: clientes.email,
            album: clientes.phone

        })
        this.abrirModal();
    })
 }

 adicionaMusica = (playlist) => {
    fetch("http://localhost:8080/playlist",      
    {method: 'POST',
    headers: {'content-type' : 'application/json'},
    body: JSON.stringify(playlist)
      })
 .then(resposta => {
    if(resposta.ok){
        this.buscarMusica();
    }else{
        alert('Não foi possivel adicionar a musica');
    }
 })
} 

 editarMusica = (playlist) => {
    fetch("http://localhost:8080/playlist/" + playlist.id,  
    {method: 'PUT',
    headers: {'Content-Type' : 'application/json'},
    body: JSON.stringify(playlist)
      })
 .then(resposta => {
    if(resposta.ok){
        this.buscarMusica();
    }else{
        alert('Não foi possivel editar os dados da musica');
    }
 })
 }






      const columns = [
       
        {
          dataField: "name",
          text: "Name",
          headerStyle: {
            backgroundColor: '#0081c2',
            color: 'white',
          }
         
        },
        
        {
          dataField: "createdBy",
          text: "Phone",
          headerStyle: {
            backgroundColor: '#0081c2',
            color: 'white',
          }
        },
       
        {
          dataField: "email",
          text: "Dropdown",
          headerStyle: {
            backgroundColor: '#0081c2',
            color: 'white',
          }
        },

        
      ];


      
    const rowStyle = { backgroundColor: 'White' };
    const expandRow = {
      renderer: row => (
        <div>            
          <p><Button variant="danger" size="sm">Excluir</Button>{' '}
          <Button variant="primary" size="sm">Editar</Button></p>
        </div>
      )
    };

    return(
    <div>
    <Menu/>
    <TituloClientes/>
    <div className="Tabela">
      <BootstrapTable
        keyField="id"
        data={clientes}
        columns={columns}
        rowStyle={ rowStyle } 
        striped
        hover
        condensed
        pagination={paginationFactory()}
        expandRow={ expandRow }
      />
    </div>
  <Footer/>
    </div>
    )
}
export default Clientes;
