import React, { useState } from 'react'
import {Button, Form} from "react-bootstrap"
import ApiCliente from "./ApiCliente";
import Menu from '../Menu/Menu';
import TituloClientes from './TituloClientes';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function CadastroClientes() {
  const navigate = useNavigate();
  const [id] = useState(null);
  const [company, setCompany] = useState('');
  const [name, setName] = useState('');
    const [createdBy, setCreatedBy] = useState('');
    const [updatedBy, setUpdatedBy] = useState('');
  

    const postData = () => {
        axios.post('http://simeiapi.doistech.com.br/api/cliente', {
          id, 
          company,
          name,
          createdBy,
          updatedBy
        }).then(() => {
          navigate('./Tabela2')
        
      })
    }

    return (
        <div>
           <Menu/>
    <TituloClientes/>
    <br></br>
                  <Form>
                  <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
        <Form.Label>Email address</Form.Label>
        <Form.Control type="text" placeholder="company" value={company} onChange={e=> setCompany(e.target.value)}/>
      </Form.Group>
      <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
        <Form.Label>Email address</Form.Label>
        <Form.Control type="text" placeholder="name" value={name} onChange={e=> setName(e.target.value)}/>
      </Form.Group>
      <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea1">
        <Form.Label>Example textarea</Form.Label>
        <Form.Control type="text" placeholder="CreatedBy" value={createdBy} onChange={e=> setCreatedBy(e.target.value)} />
      </Form.Group>
      <Form.Group className="mb-3" controlId="exampleForm.ControlTextarea2">
        <Form.Label>Example textarea</Form.Label>
        <Form.Control type="text" placeholder="UpdatedBy" value={updatedBy}  onChange={e=> setUpdatedBy(e.target.value)} />
      </Form.Group>
      <Button onClick={postData} type='submit'>Submit</Button>
    </Form>
        </div>
    )
}
