import React from "react"
import {Table, Button, Form, Modal} from "react-bootstrap"
import 'bootstrap/dist/css/bootstrap.min.css';
import Menu from "../Menu/Menu"
//import './Style.css';




class Tabela2 extends React.Component{
    constructor(props){
        super(props);
    
        this.state={
            id: 0,
            name : "",
            phone: "",
            email: "",
            clientes : [],
         
        }
     }

     componentDidUpdate(){
      this.getClientes();
     }

     getClientes= () =>{
        fetch("https://simeiapi.doistech.com.br/api/cliente/show?companyId=2")
        .then(resposta => resposta.json())
        .then(dados => {
            this.setState({clientes : dados})
        })
     }

     renderTabela(){
        return(    
        <div>
        <Table striped bordered hover>
            <thead id="tabela">
                <tr>
                    <th>id</th>
                    <th>Faixa</th>
                    <th>Autor</th>
                    <th>Album</th>
                    <th>Opções</th>
                </tr>
            </thead>
            <tbody>

              {
                this.state.clientes.map((cliente) =>
                    <tr>
                      <td>{cliente.id}</td>
                      <td>{cliente.name}</td>
                      <td>{cliente.email}</td>
                      <td>{cliente.phone}</td>
                      <td><Button variant="primary" size="sm" onClick={() => this.carregarDados(cliente.id)}>Editar</Button>{' '}
                          <Button variant="danger" size="sm" onClick={() => this.excluirMusica(cliente.id)}>Excluir</Button></td>
                    </tr>
                )
               }
                
            </tbody>
        </Table>
        </div>
        )
        }

        render  (){
            return(
                <div>
                <Menu/>
                {this.renderTabela()};
                </div>
            )
        }

        
    }

    export default tabela2;
